(window.webpackJsonp=window.webpackJsonp||[]).push([[13],{491:function(n,t,e){"use strict";e.r(t);var s=e(20),r=Object(s.a)({},(function(){var n=this,t=n.$createElement,e=n._self._c||t;return e("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[e("h3",{attrs:{id:"青春年少渐渐消逝的梦"}},[e("a",{staticClass:"header-anchor",attrs:{href:"#青春年少渐渐消逝的梦"}},[n._v("#")]),n._v(" 青春年少渐渐消逝的梦")]),n._v(" "),e("p",[n._v("题记：追忆似水年华，怀念消逝青春")]),n._v(" "),e("p",[n._v("弯弯的小河里\n依稀还能听到我们的欢声笑语\n小村的每一个角落\n洒满了我们的成长的足迹\n蓝蓝的天空\n小鸟在自由自在地飞翔\n　　\n年幼的我们\n坐在村外的小河边\n看河水哗哗流淌\n却不知时光也如同流水一般\n从我们身边悄悄流逝\n　\n无忧无虑的童年\n转眼即逝\n不知何时\n原本青涩和幼稚的脸上\n多了一些成熟和稳重\n回头看来时的路\n却发现\n再也找不到回去的方向\n昔日的小村 庄\n早已不见了当年模样\n曾经一起玩耍嬉戏的少年\n不知不觉间\n就要各奔东西")]),n._v(" "),e("p",[n._v("这些年来\n风也过\n雨也走\n年少轻狂的我们\n一路高歌\n憧憬着未来\n然而在现实面前我们不得不低下自己高昂的头颅\n但是我们依然没有停止前进的脚步\n在一起的那些点点滴滴\n在一起的那些携手并肩\n在一起的那些患难与共\n总是令我难以忘怀\n以为友谊没有永恒\n彼此之间的那份感情经受住了\n无情岁月的考验\n如同美酒一般\n变得愈发的醇香")]),n._v(" "),e("p",[n._v("如今的我们少聚多散\n越来越难再见上一面\n才发现那些青春年少的美好时光\n已渐行渐远\n徒留淡淡的回忆\n但是依然不能忘记和我一起成长的伙伴\n依然不能忘记一起成长的那些日子\n……\n后记：美好的青春年少总是那么容易逝去！谨以此献给在我青春成长路上给予我无私帮助的朋友们！因为你们陪伴，我有了美好的成长时光！因为你们的良言，我不再贪玩任性。因为你们的无私，我学会了宽容和理解。无论到了哪里，我都会将你们牢记！（敝人拙作）")])])}),[],!1,null,null,null);t.default=r.exports}}]);